class.thread_content@tag.blockquote||tag.video@src||class.thread_content@tag.img@src
<js>
result=result.replace(/cdn-khzy-i-bofang/g,"cdn-khzy-k-bofang");
result=result.replace(/khzymt/g,"khzymtu");

var html="";
if (/^内容被隐藏/.test(result)){
html = result;
}else if(/m3u8$/.test( result)){
html = "<video src=\"" + result + "\" width=\"100%\" height=\"285px\" controls=\"controls\" ></video>";
}else{
var pics=result.split("\n");
for(i=0;i<pics.length-1;i++){
html+="<img src=\""+pics[i]+"\"/>";
}
}


result = "<html><head><meta charset=\"utf-8\"><meta name=\"viewport\" content=\"width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no\" /><style>html,body{margin:0;padding:0;width:100%;}img{width:100%;margin:2px 0;}</style></head><body>" + html + "</body></html>"
</js>

