class.chapter__item@tag.a@href
@js:
var chapts=java.ajaxAll(result.split('\n').map(c=>"http://www.mh857.cn"+c));
var imgs=[];
for(var i=0;i<chapts.length;i++){
      var html=chapts[i].body();
      html=html.replaceAll('\n','').replaceAll('\t','').replaceAll('\r','').replaceAll(' ','');
      html=html.substr(html.indexOf('class="rd-article-wr')+45);
      html=html.substring(0,html.indexOf('class="rd-article__end')-4);
      var igs=html.split('</div><divid');
      for(var j=0;j<igs.length;j++){
            var ig=igs[j];
             ig=ig.substr(ig.indexOf('data-original="')+15);
             ig=ig.substring(0,ig.indexOf('"src="'))
             imgs.push(ig)
      }
}


var h=imgs.map(d => "<img src='" + d + "'/>").join('');


result="<html><head><meat charset=\"utf-8\"><meat name=\"viewport\" content=\"width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no\"/><style>html,body,*{margin:0;padding:0;width:100%;}img{width:100%;}h1{text-align: center;padding: 2em 0;}</style></head><body>"+h+"</body></html>"

